import React from "react";
import MainPhoto from "/Users/igorlevocko/Desktop/ReactLab-lab_react2/lab_react2/src/Images/main_photo.jpeg";
import styles from "./Home.styled";
import Diamond from "/Users/igorlevocko/Desktop/ReactLab-lab_react2/lab_react2/src/Images/diamond1.jpeg";
import ACER from "../../Images/ACER.jpg";
import Lenovo from "../../Images/Lenovo.jpg";
import MSI from "../../Images/MSI.jpg"
import HP from "../../Images/HP.jpg"
import Dell from "../../Images/Dell.jpg"
import CardItem from "../../components/CardItem/CardItem";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import { useState } from "react";

const data = [
    {
        mark: "Diamond",
        carat: "i5",
        image: Diamond,
        description: "GTX 970",
    },
    {
        mark: "ASUS",
        carat: "i5",
        image: ASUS,
        description: "GTX 970",
    },
    {
        mark: "ASUS",
        carat: "i5",
        image: ASUS,
        description: "GTX 970",
    },
];

const moreDate = [
    {
        mark: "ASUS",
        carat: "i5",
        image: ASUS,
        description: "GTX 970",
    },
    {
        mark: "ASUS",
        carat: "i5",
        image: ASUS,
        description: "GTX 970",
    },
    {
        mark: "ASUS",
        carat: "i5",
        image: ASUS,
        description: "GTX 970",
    },
]

const Home = () => {

    const [ViewMoreOpened, setViewMoreOpened] = useState(false);

    const viewMore = () => {
        setViewMoreOpened((prevViewMoreOpened) => !prevViewMoreOpened);
    }

    return (
        <div className="container">
            <div style={styles.container} className="row">
                <div className="col-md-6">
                    <img style={styles.img}
                        src={MainPhoto}
                        alt="Зображення"
                        className="img-fluid"
                    />
                </div>
                <div className="col-md-6">
                    <h2 style={styles.textHeader}>Магазин коштовних каменів</h2>
                    <p style={styles.text}>
                    В нашому магазині великий асортимент коштовних каменів та прикрас
                    </p>
                </div>
            </div>
            <div style={styles.cardWrapper}>
                {data.map(({mark, carat, image, description }, idx) => (
                    <CardItem
                    mark={mark}
                    carat={carat}
                    imageSrc={image}
                    description={description}
                    key={idx}
                    />
                ))}
            </div>
            {ViewMoreOpened && (
                <div style={styles.cardWrapper}>
                    {moreDate.map(({ mark, carat, image, description }, idx) => (
                        <CardItem
                        mark={mark}
                        carat={carat}
                        imageSrc={image}
                        description={description}
                        key={idx}
                    />
                ))}
                </div>
            )}
            <div style={styles.button_container}>
                <button style={styles.button} className="btn btn-primary" onClick={viewMore}>
                    View more
                </button>
            </div>
        </div>
    );
}

export default Home
