// Layout.js
import React from "react";
import StoneLogo from "/Users/igorlevocko/Desktop/ReactLab-lab_react2/lab_react2/src/Images/logo.png";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import styles from './Layout.styled';
import {
    useNavigate,
} from "react-router-dom";

const Layout = (props) => {
    const navigate = useNavigate();

    const catalogPage = () => {
        navigate('catalog');
    }

    const homePage = () => {
        navigate('/')
    }

    return (
        <header className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container">
                <a className="navbar-brand" href="/">
                    <img style={styles.logo} src={StoneLogo} alt="logo" />
                </a>
                <ul className="navbar-nav mx-auto">
                    <li style={styles.navLink} className="nav-item">
                        <button className="nav-link" onClick={homePage}>Home</button>
                    </li>
                    <li style={styles.navLink} className="nav-item">
                        <button className="nav-link" onClick={catalogPage}>Catalog</button>
                    </li>
                    <li style={styles.navLink} className="nav-item">
                        <button className="nav-link">Cart</button>
                    </li>
                    <li style={styles.navLink} className="nav-item">
                        <div className="filter-wrapper">
                            <div className="filter-item">
                                <input type="checkbox" id="filter1" name="filter1" />
                                <label htmlFor="filter1">За назвою</label>
                            </div>
                            <div className="filter-item">
                                <input type="checkbox" id="filter2" name="filter2" />
                                <label htmlFor="filter2">За кількістю карат</label>
                            </div>
                            <button className="apply-filter">Застосувати</button>
                        </div>
                    </li>
                </ul>   
                {props.searchLine && (
                    <div className="search-box">
                        <input 
                            type="text" 
                            placeholder="Пошук..." 
                            style={{ borderRadius: '10px' }}
                            onChange={(e) => props.onSearch(e.target.value)}
                        />
                    </div>
                )}
            </div>
        </header>
    );
}

export default Layout;
