// Layout.js
import React from "react";
import StoneLogo from "/Users/igorlevocko/Desktop/ReactLab-lab_react2/lab_react2/src/Images/logo.png";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import styles from './Layout.styled';
import {
    useNavigate,
} from "react-router-dom";

const Layout = (props) => {
    const navigate = useNavigate();

    const catalogPage = () => {
        navigate('catalog');
    }

    const homePage = () => {
        navigate('/')
    }

    const handleFilter1 = () => {
        props.onApplyFilters('alphabetical');
    }

    const handleFilter2 = () => {
        props.onApplyFilters('carat');
    }

    return (
        <header className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container">
                {/* ... */}
                <ul className="navbar-nav mx-auto">
                    {/* ... */}
                </ul>   
                {props.searchLine && (
                    <div className="search-box">
                        <input 
                            type="text" 
                            placeholder="Пошук..." 
                            style={{ borderRadius: '10px' }}
                            onChange={(e) => props.onSearch(e.target.value)}
                        />
                    </div>
                )}
                <div>
                    <button onClick={handleFilter1}>Фільтр 1</button>
                    <button onClick={handleFilter2}>Фільтр 2</button>
                    <button>Застосувати</button>
                </div>
            </div>
        </header>
    );
}

export default Layout;
